import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col
} from "reactstrap";

import React from "react";
import { useRef, useState, useEffect, useContext } from 'react';
import AuthContext from '../../context/AuthProvider';
import axios from '../../api/axios';
import { useLocation, Route, Switch, Redirect } from "react-router-dom";
import routes from "routes.js";

const LOGIN_URL = '/users/login';

const Login = () => {
    const { setAuth } = useContext(AuthContext);
    const userRef = useRef();
    const errRef = useRef(); 

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);
    const [role, setRole] = useState('');
    const [token, setToken] = useState('');
    const [layoutName, setLayoutName] = useState('user');

    useEffect(() => {
      const getRoutes = (routes) => {
        return routes.map((prop, key) => {
          if (prop.layout === layoutName) {
            return (
              <Route
                path={prop.layout + prop.path}
                component={prop.component}
                key={key}
              />
            );
          } else {
            return null;
          }
        });
      };
    });

    const handleSubmit = async (e) => {
        e.preventDefault();

         try {
            let loginrequest = JSON.stringify({
                email: user, 
                password: pwd});

            const response = axios.post(LOGIN_URL, loginrequest, {
                headers: { 'Content-Type': 'application/json' },
            });

            let data;
            await response.then(result => {
                return data = result.data;
            });

            setRole(data['value']['roleName']);
            setToken(data['value']['token']);
            localStorage.setItem('token', JSON.stringify(data['value']['token']));
            setAuth({ user, pwd, role, token });
            setUser('');
            setPwd('');
            setSuccess(true);

         } catch (err) {
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 400) {
                setErrMsg('Missing username or password');
            } else if (err.response?.status === 401) {
                setErrMsg('Unauthorized');
            } else {
                setErrMsg('Login Failed');
            }
         }
        }
    return (
          <>
          {success ? (
            <section>
                <h1>Giriş yaptın!</h1>
                <br />
                <p>
                    (<Switch>
                      {getRoutes(routes)}
                      <Redirect from="*" to={layoutName} />
                    </Switch>)
                </p>
            </section>
        ) : (
            <Col lg="5" md="7" onSubmit={handleSubmit}>
              <Card className="bg-secondary shadow border-0">
                <CardBody className="px-lg-5 py-lg-5">
                  <Form role="form">
                    <FormGroup className="mb-3">
                      <InputGroup className="input-group-alternative">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="ni ni-email-83" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input ref={userRef} onChange={(e) => setUser(e.target.value)} value={user} required
                          placeholder="Email"
                          type="email"
                          autoComplete="new-email"
                        />
                      </InputGroup>
                    </FormGroup>
                    <FormGroup>
                      <InputGroup className="input-group-alternative">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="ni ni-lock-circle-open" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input onChange={(e) => setPwd(e.target.value)} value={pwd} required
                          placeholder="Password"
                          type="password"
                          autoComplete="new-password"
                        />
                      </InputGroup>
                    </FormGroup>
                    <div className="custom-control custom-control-alternative custom-checkbox">
                      <input
                        className="custom-control-input"
                        id=" customCheckLogin"
                        type="checkbox"
                      />
                      <label
                        className="custom-control-label"
                        htmlFor=" customCheckLogin"
                      >
                        <span className="text-muted">Remember me</span>
                      </label>
                    </div>
                    <div className="text-center">
                      <Button className="my-4" color="primary" type="submit" onClick={handleSubmit}>
                        Sign in
                      </Button>
                    </div>
                  </Form>
                </CardBody>
              </Card>
            </Col>
        )}
          </>
        )
    }

export default Login;
